var FileDB = new PouchDB('dbname', { auto_compaction : true });
var write_lock = new Mutex();
FileDB._cache = {}
FileDB.record_rev_cache = function(doc){
	
	FileDB._cache["last_rev_" + doc._id ] = doc._rev 
}
var db_damage = _.throttle(function(){
	toastr.error(MSG("DB_DAMAGE"))
}, 10 * 1000)
FileDB.update = function(id, value, attachments, shrink, times){
	var self = this;
	var defer = $.Deferred();
	var value_back = value
	write_lock.timedLock(30 * 1000, function(){
		self.get(id, function(err, old_doc){
			var old_rev = (old_doc || {})._rev
			var cached_rev = FileDB._cache["last_rev_" + id]

			if (!attachments && old_doc && old_doc._attachments){
				value._attachments = old_doc._attachments
			}
			if (attachments){
				console.debug('[filedb]attachment', attachments)
				value._attachments = attachments;
			}
			
			if (shrink){
				value = _.pick(old_doc, ["fid", "guid", "status", "title", "notebook", "tagNames"])
				value.shrinked = true;
				console.debug("shrink");
				// TMP LocalDB.del(id)	
			} else {
				// TMP (old_rev % 4) || (value.md && value.md.replace(/(\s|\n|\r)/g, '').length >10 && LocalDB.set(id, value.md))	
			}

			console.debug("update", old_rev)
			self.put(value, id, old_rev, function(err, response){
				write_lock.unlock();
				if (err){
					logger.log({ msg : '[db]put file error', err: err, value: value })
					console.warn('save file error', err);
                    if (err.status == '409'){
                        if (times < 0){
                            console.warn('[db] final error!!!')
                            return defer.reject(err);
                        }
						if (times === 0){
                            // TMP
                            // 
                            console.warn("[db]final update error, try to remove the latest version", old_rev)
                            FileDB.remove(id, function(err, response){
                                FileDB.update(id, value_back, attachments, shrink, -1);
                            })
                              
						} else {
							times = times || 4;
							logger.log({ msg : "retry " + times })
							
							return setTimeout(function(){
								FileDB.update(id, value_back, attachments, shrink, times - 1);
							}, 1000)
						} 
					}

					if (/could not prepare statement/.test(err.message) || (/closing/.test(err.message))){
						db_damage();
					}
					defer.reject(err);	
				} else {
					FileDB.compact_document(id, response.rev);
					FileDB._cache["last_rev_" + id ] = response.rev 
					defer.resolve();
				}
				})	
			})
	})
	return defer.promise();
}
FileDB.del = function(id, value){
	var self = this;
	this.get(id, function(err, doc){
		if (err){
			return console.warn("delete file error", id)
		}
		self.remove(doc);
	})
}
