define('includes/filemanager',[
	"jquery",
	"underscore",
	"vine",
	"includes/session",
	"includes/file",
    "fileMgr",
    "includes/log",
    'utils'
], function($, _, Vine, Session, NoteFile, fileMgr, logger, utils){

	var FileManager = {}
    function to_attachment_from_html(html) {
        var img = $(html);
        $('#images_area').find('[longdesc="' + img.attr('longdesc') + '"]').remove()
        $('#images_area').append(img);
        var ret = [img.attr('longdesc'), { content_type : img.attr('type'), data : utils.seperate_base64(img.attr('src')).data }]
        img[0].src = utils.dataURI_to_blob(img[0].src);
        return ret;
    }

	FileManager.setCurrentFile = function(file){
		var old_id =  Session.tab_last_file; 
		if (old_id && old_id != file.fid ){
			Vine.trigger("CHECK_AND_REMOVE_FILE_CACHE", old_id);
		}

		Session.lock.is_conflict(file.fid, function(result){
			if (result){
				file.readonly = true;
				Vine.trigger("START_READONLY");
			} else {

				Vine.trigger("STOP_READONLY");
			}
			Session.current_file = file;

			/* TMP
			MathCache.reset();
			*/

			if (!file.readonly){
				Session.set('last_file_id', file.fid);
				/* TMP
				TagsPlugin.reset();
				*/
				file.active();
			}

			Session.tab_last_file = file.fid; 
			fileMgr.selectFile(file.get_wrapper());
			/* TMP 
			editor.getSession().setValue(file.md);
			ImageManager.restore_images()
			previewMd();
			Evernote.reset_log()
			*/
			//ImageManager.clean()
			//setTimeout(ImageManager.clean, 2000);

		})
		/*
		if (SessionLock.is_conflict(file.fid)){
			file.readonly = true;
			Vine.trigger("START_READONLY");
		} else {
			
			Vine.trigger("STOP_READONLY");
		}
		Session.current_file = file;


		if (!file.readonly){
			Session.set('last_file_id', file.fid);
			Session.tab_last_file = file.fid; 
			TagsPlugin.init();
			file.active();
		}

		editor.getSession().setValue(file.md);
		ImageManager.restore_images()
		previewMd();
		*/
	}
	FileManager.createFile = function(options){
		// just in case of combo empty file
		if (!options && Session.current_file && Session.current_file.is_empty()){
			Session.current_file.md = ""
			this.setCurrentFile(Session.current_file);

		} else {
			var file = new NoteFile(options);
			if (options && options._images){
				file._raw_attachments = options._images.reduce(function(last, i){
					var attach = to_attachment_from_html(i);
					file.images.push(attach[0])
					last[attach[0]] = attach[1]
					return last;
				}, {})
				logger.push('open from Evernote')
				logger.push(options)
				logger.push(file.images);
				delete options._images;
			}
			
			Vine.trigger('CHANGE_FILE', file)
			//this.setCurrentFile(file);

		}
	}

	FileManager.open_file = function(id, remote){
		var self = this
		return Session.load_file(id).then(function(file){
			if (file.shrinked){
				return Vine.trigger("FETCH_NOTE", file.guid)
			}
			file.merge_remote(remote);
			Vine.trigger('CHANGE_FILE', file)
			//self.setCurrentFile(file);
		}).fail(function(){
			// TODO
			var file = Session.files[id] || {}
			if (file.guid){
				file.shrinked = true;
				return Vine.trigger("FETCH_NOTE", [file.guid, file])
			}
			toastr.error(MSG("ERROR_OPEN_FILE"));
			logger.end()
		})
	}

	FileManager.deleteFile = function(fid){
		var file = Session.files[fid]
		file && Session.delete_file(file);
	}
	Vine.watch('files', function(newValue){
		var filelist = _.toArray(newValue).reverse().map(function(file){
			file["is_" + file.status] = true;
			if (file.status != 'created'){
				if (file.notebook) {
					file['saved_notebook'] = file.notebook;
				} else {
					file['default_notebook'] = true
				}
				
			}
			if (file.fid == (Session.tab_last_file || Session.last_file_id)){
				file.is_current = true;
			} else {
				file.is_current = false;
			}
			return file;
		
		}).sort(function(a, b){
			return calculate(b) - calculate(a);	
		})
		Vine.trigger_value('filelist', filelist)

		$('.document-list [title]').tooltip()

		function calculate(i){
			var score = 0;
			score += (parseInt(i.fid) || 0);
			if (i.status != 'synced'){
				score += 1e13;
			}
			/*
			if (i.fid == Session.last_file_id){
				score += 1e13;
			}
			*/
			return score;
		}
	})

	FileManager.newFile = function(args){
		return new NoteFile(args)
	}
	return FileManager;
})
